export function Validation(inputs){
    let validation = {}
if (!inputs.name) {
    validation.name = "Es necesario un nombre"
}
if (inputs.score > 100 || inputs.score < 0) {
    validation.score = "La puntuacion no es correcta, debe ser mayor a 0 y menor que 100"
}
}
