import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { getDiets } from "../../redux/actions";
import { postRecipe } from "../../redux/actions";
export const NewRecipes = () => {

    const diets = useSelector(state => state.diets);
    const dispatch = useDispatch()
    useEffect(() => {
        dispatch(getDiets())
    },[dispatch])

    const [recipes, setRecipes] = useState({
        name:"",
        resumen:"",
        score:"",
        stepByStep:"",
        dietId:[]

    })

    function handleOnchange(e){
        setRecipes({
            ...recipes,
            [e.target.name]: e.target.value
        })
        console.log(recipes)
    }

    function handleSubmit(e){
        e.preventDefault()
        postRecipe(recipes)
    }

    function handelCheck(e){
        if(e.target.checked){
            setRecipes({
                ...recipes,
                dietId: [...recipes.dietId,e.target.value]
            })
            console.log(recipes)
        }
    }

    return (
        <div>
            <h1>Nueva Receta</h1>
            <form onSubmit={handleSubmit}>
                <label htmlFor="name">Nombre</label>
                <br />
                <input 
                    type="text"
                    name="name"
                    placeholder="Nombre de la receta"
                    value={recipes.name}
                    onChange={handleOnchange}
                />
                <br />
                <label htmlFor="score">Puntuacion</label>
                <br />
                <input 
                    type="number"
                    name="score"
                    value={recipes.score}
                    placeholder="0"
                    defaultValue="0"
                    min="0"
                    max="100"
                    onChange={handleOnchange}
                />
                <br />
                <label>Tipos de Dieta</label>
                <br />
                <div>
                    {
                        diets.map(diet => {
                            return(
                                <label>
                                    <input 
                                        type="checkbox" 
                                        name="dietId" 
                                        value={diet.id}
                                        onChange={(e) => handelCheck(e)}
                                        />
                                        {diet.name}
                                    <br />
                                </label>
                            )
                        })
                    }
                </div>
                <br />
                <label htmlFor="summary">Paso a Paso</label>
                <br />
                <textarea
                    onChange={handleOnchange}
                    value={recipes.stepByStep}
                    name="stepByStep" 
                    cols="30" 
                    rows="10"> 
                    
                </textarea>
                <br />
                <label htmlFor="summary">Resumen</label>
                <br />
                <textarea 
                    onChange={handleOnchange}
                    value={recipes.resumen}
                    name="resumen" 
                    cols="30" 
                    rows="10">
                    
                </textarea>
                <br />
                <div>
                    <button type="submit">Crear</button>
                </div>
            </form>
        </div>
    )
}
