                // case '20-0':
                // n1=20;
                // n2=0;
                // return (n1,n2)

                default: {n1 = 1; n2=0}
            }
            console.log(n1,n2)
            let filterByScore = state.recipes.filter(el => el.score <= 100 && el.score >= 80 )
            return{
                ...state,
                recipes: filterByScore
            }
        }
        default: return state
    }
}
