const initialState ={
    recipes: []
}

export function rootReducer (state = initialState, action)  {
    switch(action.type){
        case 'GET_ALL_RECIPES':
            return{
                ...state,
                recipes: action.payload
        }
        case 'FILTER_BY_DIETS':
            const allRecipes = state.recipes;
            const diets = action.payload;
            const filterByDiets = diets === 'all' ? allRecipes : allRecipes.filter( el => el.diet.includes(diets));
            return{
                ...state,
                recipes: filterByDiets
            }
        case 'FILTER_BY_DESC_ASC':
            const allRecipesby = state.recipes;
            const asc = allRecipesby.sort(el => el.name);
            const desc = allRecipesby.map(el => el.name)
            const filter = action.payload
            const filterbyDescAsc = filter === 'asc' ? asc : desc
            return{
                ...state,
                recipes: filterbyDescAsc
            }
        default: return state
    }
}
